def parse_html_data(html_text):
    soup = BeautifulSoup(html_text, "html.parser")
    #Parse Data
    text = soup.get_text()

    
    #Remove Greeting
    text = re.sub(r'[\W_]*Regards[\W_]*Trendy[\W_]*tech[\W_]*team[\W_]*Please[\W_]*rate[\W_]*your[\W_]*experience[\W_]*with[\W_]*our[\W_]*Support[\W_]*Team[\W_]*\.?[\W_]*Your[\W_]*feedback[\W_]*will[\W_]*help[\W_]*us[\W_]*improve[\W_]*our[\W_]*support[\W_]*services[\W_]*\.?[\W_]*Good[\W_]*Okay[\W_]*Bad[\W_]*', '\n', text, flags=re.IGNORECASE)

    #Remove Details of who wrote what
    text = re.sub(r'(?i)On \b(Mon|Tue|Wed|Thu|Fri|Sat|Sun)\b[\W_]*.*?wrote[\W_]*?','\n', text)

    text = text.replace('\n ---- ',"*Customer=")
    text = text.replace('\n:',"\n*Support=")

    return (text)

with open('latest_thread.txt', 'w', encoding='utf-8') as f:
    f.write(parse_html_data(html))
